// In-Memory Cache Implementation (Redis alternative for development)
class SimpleCache {
  constructor() {
    if (!global.chatbotCache) {
      global.chatbotCache = new Map();
      global.cacheTimestamps = new Map();
    }
  }

  get(key) {
    const now = Date.now();
    const timestamp = global.cacheTimestamps.get(key);

    // Check if expired (10 minutes = 600000ms)
    if (!timestamp || (now - timestamp) > 600000) {
      this.delete(key);
      return null;
    }

    return global.chatbotCache.get(key);
  }

  set(key, value) {
    // Limit cache size to 1000 entries
    if (global.chatbotCache.size >= 1000) {
      const firstKey = global.chatbotCache.keys().next().value;
      this.delete(firstKey);
    }

    global.chatbotCache.set(key, value);
    global.cacheTimestamps.set(key, Date.now());
  }

  delete(key) {
    global.chatbotCache.delete(key);
    global.cacheTimestamps.delete(key);
  }

  clear() {
    global.chatbotCache.clear();
    global.cacheTimestamps.clear();
  }
}

