TZOFFSETFROM:+0000
TZOFFSETTO:+0100
TZNAME:WEST
DTSTART:19700329T010000
RRULE:FREQ=YEARLY;BYMONTH=3;BYDAY=-1SU
END:DAYLIGHT
BEGIN:STANDARD
TZOFFSETFROM:+0100
TZOFFSETTO:+0000
TZNAME:WET
DTSTART:19701025T020000
RRULE:FREQ=YEARLY;BYMONTH=10;BYDAY=-1SU
END:STANDARD
END:VTIMEZONE
BEGIN:VEVENT
UID:${eventId}
DTSTAMP:${formatVEventDate(new Date())}
DTSTART;TZID=Europe/Lisbon:${formatVEventDate(startDateTime).slice(0, -1)}
DTEND;TZID=Europe/Lisbon:${formatVEventDate(endDateTime).slice(0, -1)}
SUMMARY:${eventTitle}
DESCRIPTION:${eventDescription.replace(/\n/g, '\\n')}
LOCATION:Online/Office
STATUS:TENTATIVE
CLASS:PRIVATE
TRANSP:OPAQUE
${attendeeEmail ? `ATTENDEE;CN=${attendeeName};RSVP=TRUE:mailto:${attendeeEmail}\n` : ''}ORGANIZER;CN=AI Assistant:mailto:${process.env.ADMIN_EMAIL || 'admin@company.com'}
CATEGORIES:AI-Generated,${appointmentType}
END:VEVENT
END:VCALENDAR`;

return [{
  json: {
    ...appointmentData,
    vevent,
    eventTitle,
    eventDescription,
    startDateTime: startDateTime.toISOString(),
    endDateTime: endDateTime.toISOString(),
    eventId,
    calendarData: {
      title: eventTitle,
      start: startDateTime.toISOString(),
      end: endDateTime.toISOString(),
      description: eventDescription,
      attendees: emails,
