      status: 'completed',
      description: 'In-memory cache cleared successfully'
    }
  },
  summary: {
    totalTasksScheduled: 3,
    totalTasksCompleted: [
      getTaskStatus(conversationCleanup) === 'completed' ? 1 : 0,
      getTaskStatus(appointmentCleanup) === 'completed' ? 1 : 0,
      1 // cache always succeeds
    ].reduce((a, b) => a + b, 0),
    totalTasksFailed: [
      getTaskStatus(conversationCleanup) === 'failed' ? 1 : 0,
      getTaskStatus(appointmentCleanup) === 'failed' ? 1 : 0
    ].reduce((a, b) => a + b, 0),
    totalRecordsDeleted: (conversationCleanup.count || 0) + (appointmentCleanup.count || 0)
  }
};

// Add overall status
report.overallStatus = report.summary.totalTasksFailed === 0 ? 'success' : 'partial_failure';

return [{
  json: report
}];

