// Security Validator - Input Sanitization & Validation
const crypto = require('crypto');

// Get input data
const inputData = $input.all();
const body = inputData[0].body || {};

// Security validation patterns
const sqlInjectionPattern = /('|(\-\-)|(;)|(\||\||)|(\*|\*)|(%7C))/i;
const xssPattern = /<[^>]*>?/gm;
const dosPattern = /(.{1000,})/; // Basic DoS protection

// Validate required fields
if (!body.message || typeof body.message !== 'string') {
  throw new Error('Invalid or missing message field');
}

// Security checks
if (sqlInjectionPattern.test(body.message) || 
    xssPattern.test(body.message) || 
    dosPattern.test(body.message)) {
  throw new Error('Security validation failed');
}

// Sanitize input
const sanitizedMessage = body.message
  .trim()
  .substring(0, 1000) // Limit message length
  .replace(/[<>&"']/g, (match) => {
    const escapeMap = {
      '<': '&lt;',
      '>': '&gt;',
      '&': '&amp;',
      '"': '&quot;',
      "'": '&#x27;'
    };
    return escapeMap[match];
  });

// Generate session hash for caching (GDPR-compliant)
const sessionId = body.sessionId || 'anonymous';
const messageHash = crypto.createHash('sha256')
  .update(sanitizedMessage + sessionId)
  .digest('hex');

// Language detection
const language = body.language || 'pt'; // Default to Portuguese
if (!['pt', 'en'].includes(language)) {
  throw new Error('Unsupported language');
}

// Prepare validated data
const validatedData = {
  message: sanitizedMessage,
  language: language,
  sessionId: sessionId,
  messageHash: messageHash,
  timestamp: new Date().toISOString(),
  userAgent: body.userAgent || 'unknown',
  ip: $input.item().headers['x-forwarded-for'] || 'unknown'
};

return [{ json: validatedData }];
