// Enhanced GDPR-compliant daily maintenance with detailed logging
const crypto = require('crypto');

// Calculate cleanup dates
const now = new Date();
const thirtyDaysAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000).toISOString();
const ninetyDaysAgo = new Date(now.getTime() - 90 * 24 * 60 * 60 * 1000).toISOString();

// Clear in-memory cache safely
if (typeof global !== 'undefined' && global.chatbotCache) {
  global.chatbotCache.clear();
  global.cacheTimestamps.clear();
}

// Define maintenance tasks with enhanced metadata
const maintenanceTasks = {
  conversationCleanup: {
    table: 'conversations',
    cutoffDate: thirtyDaysAgo,
    description: 'Delete conversations older than 30 days (GDPR compliance)',
    priority: 'high'
  },
  appointmentCleanup: {
    table: 'appointments', 
    cutoffDate: ninetyDaysAgo,
    description: 'Delete completed appointments older than 90 days',
    priority: 'medium'
  },
  cacheCleanup: {
    description: 'Clear in-memory cache and reset counters',
    completed: true,
    priority: 'low'
  }
};

return [{
  json: {
    maintenanceTasks,
    timestamp: now.toISOString(),
    timezone: 'Europe/Lisbon',
    maintenanceId: crypto.randomUUID(),
    status: 'ready'
  }
}];

// Enhanced maintenance reporting with comprehensive analytics
const maintenanceData = $input.item(0).json;
const conversationCleanup = $input.item(1).json;
const appointmentCleanup = $input.item(2).json;

// Calculate success/failure status
function getTaskStatus(result) {
  if (result.error) return 'failed';
  if (result.status && result.status >= 200 && result.status < 300) return 'completed';
  return 'completed'; // Default to completed if no error
}

const report = {
  maintenanceId: maintenanceData.maintenanceId,
  timestamp: new Date().toISOString(),
  timezone: 'Europe/Lisbon',
  duration: {
    start: maintenanceData.timestamp,
    end: new Date().toISOString()
  },
  tasks: {
    conversationCleanup: {
      status: getTaskStatus(conversationCleanup),
      recordsAffected: conversationCleanup.count || 0,
      error: conversationCleanup.error || null,
      cutoffDate: maintenanceData.maintenanceTasks.conversationCleanup.cutoffDate
    },
    appointmentCleanup: {
      status: getTaskStatus(appointmentCleanup),
      recordsAffected: appointmentCleanup.count || 0,
      error: appointmentCleanup.error || null,
      cutoffDate: maintenanceData.maintenanceTasks.appointmentCleanup.cutoffDate
    },
    cacheCleanup: {
      status: 'completed',
      description: 'In-memory cache cleared successfully'
    }
  },
  summary: {
    totalTasksScheduled: 3,
    totalTasksCompleted: [
      getTaskStatus(conversationCleanup) === 'completed' ? 1 : 0,
      getTaskStatus(appointmentCleanup) === 'completed' ? 1 : 0,
      1 // cache always succeeds
    ].reduce((a, b) => a + b, 0),
    totalTasksFailed: [
      getTaskStatus(conversationCleanup) === 'failed' ? 1 : 0,
      getTaskStatus(appointmentCleanup) === 'failed' ? 1 : 0
    ].reduce((a, b) => a + b, 0),
    totalRecordsDeleted: (conversationCleanup.count || 0) + (appointmentCleanup.count || 0)
  }
};

// Add overall status
report.overallStatus = report.summary.totalTasksFailed === 0 ? 'success' : 'partial_failure';

return [{
  json: report
}];

