if (auth !== expected) {
  throw new Error('Unauthorized');
}
return items;

// Basic rate limiting (per IP)
const redis = require('redis');
const ip = $headers['x-forwarded-for'] || $headers['host'] || 'unknown';
const r = global.get('redis');
const key = `ratelimit:${ip}`;
const limit = parseInt($env.RATE_LIMIT_PER_MIN) || 60;
const ttl = 60;
return new Promise((resolve, reject) => {
  r.incr(key, (err, count) => {
    if (err) return reject(err);
    if (count === 1) r.expire(key, ttl);
    if (count > limit) return reject(new Error('Rate limit exceeded'));
    resolve(items);
  });
});

// Input security & sanitization
const crypto = require('crypto');
const userMessage = $input.first().json.body?.message?.trim() || '';
if (!userMessage) throw new Error('Empty message');
const sessionId = $input.first().json.body?.sessionId || crypto.randomUUID();
return [{json: {originalMessage: userMessage, sessionId}}];

// Language detection (GDPR compliant)
const franc = require('franc');
const msg = $input.first().json.originalMessage;
const lang = franc(msg);
return [{json: {...$input.first().json, detectedLanguage: lang}}];

// Redis cache lookup
const redis = require('redis');
const r = global.get('redis');
const key = `chatbot:cache:${$input.first().json.sessionId}:${Buffer.from($input.first().json.originalMessage).toString('base64')}`;
return new Promise((resolve, reject) => {
  r.get(key, (err, val) => {
    if (err) reject(err);
    if (val) resolve([{json: JSON.parse(val), $continue: false}]);
    resolve([{$json: {...$input.first().json, cacheKey: key}}]);
  });
});

